import {voltLevels1,
        voltLevels2,
        voltLevels3,
        substations1,
        substations2,
        substations3,
        substations4,
        substations5,
        substations6,
        branches} from './mockDataTree'

import {
  tableBranch1,
  tableBranch2,
  tableBranch3
} from './mockDataTables'

export const getTableData = async (str) =>
{
  let result=null
  switch (str) {
    case '/branch/1':
      result = tableBranch1
      break
    case '/branch/2':
      result = tableBranch2
      break
    case '/branch/3':
      result =  tableBranch3
      break
    default:
      break
    }
    if(result)
    {
      return new Promise((resolve, reject) =>{
        resolve(result)
        //reject('error')
      })
    }
    else
    {
      return new Promise((resolve, reject) =>{
        //resolve(result)
        reject('api tables error')
      })
    }
}

export const initData = branches

export const getTreeData = async (str) =>
{
  let result=null
  switch (str) {
    case 'getVoltLevels/branch/1':
      result = voltLevels1
      break
    case 'getVoltLevels/branch/2':
      result = voltLevels2
      break
    case 'getVoltLevels/branch/3':
      result =  voltLevels3
      break
    case 'getSubstations/branch/1/voltLevel/2':
      result =  substations1
      break
    case 'getSubstations/branch/1/voltLevel/3':
      result =  substations2
      break
    case 'getSubstations/branch/2/voltLevel/5':
      result =  substations6
      break
    case 'getSubstations/branch/3/voltLevel/1':
      result = substations3
      break
    case 'getSubstations/branch/3/voltLevel/2':
      result =  substations4
      break
    case 'getSubstations/branch/3/voltLevel/3':
      result = substations5
      break
    default:
      break
    }
    if(result)
    {
      return new Promise((resolve, reject) =>{
        resolve(result)
        //reject('error')
      })
    }
    else
    {
      return new Promise((resolve, reject) =>{
        //resolve(result)
        reject('api tree error')
      })
    }
}
